package Noviembre;

public class Ejercicio9_CondicionalesBucles100_0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 100;
		while (i > 0) {
			System.out.println("El valor de i es : " + i);
			i = i - 7;
		}
		for ( i = 100; i > 0; i -= 7) {
			System.out.println("El valor de i es :" + i);


		}

	}
	}
